import React, {useState} from "react";
import { Form, Button, Col, Toast, ToastContainer } from "react-bootstrap";

const RegisterForm = () => {
    const [formData, setFormData] = useState({
      firstName: '',
      lastName: '',
      email: '',
      password: '',
      confirmPassword: '',
      sex: '',
      country: '',
    });

    const [errors, setErrors] = useState({});
    const [showToast, setShowToast] = useState(false);
    const [toastMessage, setToastMessage] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        const newErrors = {};
        if (!formData.firstName) {
          newErrors.firstName = 'First name is required';
        }
        if (!formData.lastName) {
          newErrors.lastName = 'Last name is required';
        }
        if (!formData.email  || !isValidEmail(formData.email)) {
          newErrors.email = 'Invalid email address';
        }
        if (!formData.password || formData.password.length < 6) {
          newErrors.password = 'Password must be at least 6 characters long';
        }
        if (formData.password  !== formData.confirmPassword) {
          newErrors.confirmPassword = 'Passwords do not match';  
        }
        if (!formData.sex) {
            newErrors.sex = 'Please select your sex';
          }
          if (!formData.country) {
            newErrors.country = 'Please select your country';
          }

          setErrors(newErrors);

          if (Object.keys(newErrors).length === 0) {
            // Handle form submission (e.g., send data to server)
            console.log('Form submitted successfully:', formData);
            setShowToast(true);
            setToastMessage('Registration successful!');
          }
        };
        const isValidEmail = (email) => {
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            return emailRegex.test(email);
          };

  return (
    <div>
      <Form onSubmit={handleSubmit}>
        <Form.Group as={Col} mb="4">
          <Form.Label>First Name</Form.Label>
          <Form.Control
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            isInvalid={!!errors.firstName}
          />
          <Form.Control.Feedback type="invalid">{errors.firstName}</Form.Control.Feedback>  

        </Form.Group>
        <Form.Group  
 as={Col} md="4">
          <Form.Label>Last Name</Form.Label>
          <Form.Control
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            isInvalid={!!errors.lastName}
          />
          <Form.Control.Feedback type="invalid">{errors.lastName}</Form.Control.Feedback>  

        </Form.Group>
        <Form.Group  
 as={Col} md="4">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            isInvalid={!!errors.email}
          />
          <Form.Control.Feedback type="invalid">{errors.email}</Form.Control.Feedback>  

        </Form.Group>
        <Form.Group  
 as={Col} md="4">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            isInvalid={!!errors.password}
          />
          <Form.Control.Feedback  
 type="invalid">{errors.password}</Form.Control.Feedback>  

        </Form.Group>
        <Form.Group as={Col} md="4">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}  

            isInvalid={!!errors.confirmPassword}
          />
          <Form.Control.Feedback type="invalid">{errors.confirmPassword}</Form.Control.Feedback>  

        </Form.Group>
        <Form.Group  
 as={Col} md="4">
          <Form.Label>Sex</Form.Label>
          <Form.Select
            name="sex"
            value={formData.sex}
            onChange={handleChange}
            isInvalid={!!errors.sex}
          >
            <option value="">Select</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>  

          </Form.Select>
          <Form.Control.Feedback type="invalid">{errors.sex}</Form.Control.Feedback>  

        </Form.Group>
        <Form.Group as={Col} md="4">
          <Form.Label>Country</Form.Label>
          <Form.Select
            name="country"
            value={formData.country}
            onChange={handleChange}
            isInvalid={!!errors.country}
          >
            <option value="">Select</option>
            {/* Add country options here */}
          </Form.Select>
          <Form.Control.Feedback type="invalid">{errors.country}</Form.Control.Feedback>
        </Form.Group>
        <Button variant="primary" type="submit">
          Register
        </Button>
      </Form>
      <ToastContainer position="top-end" delay={4000}>
        <Toast onClose={() => setShowToast(false)} show={showToast} delay={4000}>
          <Toast.Header>
            <strong className="me-auto">Success</strong>
          </Toast.Header>
          <Toast.Body>{toastMessage}</Toast.Body>
        </Toast>
      </ToastContainer>  

    </div>
  );
};

export default RegisterForm;
